import React, { useState, useEffect } from 'react';
import EmployeeForm from './addEmployee';
import EmployeeList from './employeeList';
import Project from './project';
import { useSelector } from 'react-redux';
import { useDispatch } from 'react-redux'
import Provider from '../context/provider';
import '../css/styles.css';

function App() {
  const employeeList = useSelector(state => state.employeeList);
  const test = [...employeeList];
  const [employees, setEmployees] = useState(test);
  const dispatch = useDispatch();


  useEffect(() => {
    localStorage.setItem('store', JSON.stringify(employeeList)); // use con data
    localStorage.getItem('store');// use persisted data
  }, [employeeList]);

  const addEmployee = name => {
    const employee = {name: name, isAbsent: false};
    dispatch({ type: 'ADD_EMPLOYEE', payload: employee });
  };

  function debounce(func, wait, immediate) {
    var timeout;

    return function executedFunction() {
      var context = this;
      var args = arguments;

      var later = function() {
        timeout = null;
        if (!immediate) func.apply(context, args);
      };

      var callNow = immediate && !timeout;

      clearTimeout(timeout);

      timeout = setTimeout(later, wait);

      if (callNow) func.apply(context, args);
    };
  };

  // marking Absent of not present
  const attendence = index => {
    const newEmployee = [...employeeList];
    newEmployee[index].isAbsent = true;
    setEmployees(newEmployee);
  };

  // Remove Employee from List
  const removeEmployee = index => {
    dispatch({ type: 'REMOVE_EMPLOYEE', payload: index });
  };
  // Remove Employee from List
  const editEmployee = index => {
    const newEmployee = [...employeeList];
    newEmployee[index].showDetail = true;
    setEmployees(newEmployee);
    console.log(employees[index], 'employees');
  };


  return (
    <div className="app">
      <div className="ui middle aligned center aligned grid">
        <div class="column">
          <h1>Attendence</h1>
          <div className="container">
            <div className="top-view">
              <Provider>
                <Project />
              </Provider>
            </div>
            <div className="container-inr">
              {employeeList.map((employee, index) => (
                <EmployeeList
                  key={index}
                  index={index}
                  employee={employee}
                  attendence={attendence}
                  removeEmployee={removeEmployee}
                  editEmployee={editEmployee}
                />
              ))}
              <EmployeeForm addEmployee={addEmployee} />
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
