import React, { Fragment, useState } from 'react';

const EmployeeDetail = ({ employee, closeModal, editEmployees, index }) => {
  const test = {name: false, id: false};
  const [showInput, setInput] = useState(test);
  // const [showInputId, setInputId] = useState(false);
  // const [showDesig, setDesig] = useState(false);
  // const [showManager, setManager] = useState(false);
  // const [showShift, setShift] = useState(false);

  const handleEdit = (value, index, key) => {
    if (!value) return;
    editEmployees(value, index, key);
  };

  return (
    <Fragment>
      <div className="modal-container-outer" onClick={() => closeModal()}></div>
      <div className="modal-container employee-detail-container">
        <small onClick={() => closeModal()}>x</small>
        <h3>{employee.name}'s Detail</h3>
        <div className="employee-detail">
          <div className="employee-list">
            <p>
              <label>Employee Name</label>
              {!showInput.name ?
                <span onClick={() => setInput({[showInput.name]: true})}>{employee.name}</span> :
                <input type="text" value={employee.name} onChange={e => handleEdit(e.target.value, index, 'name')} />
              }
            </p>
            <p>
              <label>Employee ID</label>
              {employee.employeeId && !showInput.id ?
              <span onClick={() => setInput({[showInput.id]: true})}>{employee.employeeId}</span> :
              <input type="text" value={employee.employeeId} onChange={e => handleEdit(e.target.value, index, 'employeeId')} />}
            </p>
            <p>
              <label>Designation</label>
              {employee.designation && !showDesig ?
              <span onClick={() => setDesig({showDesig: true})}>{employee.designation}</span> :
              <input type="text" value={employee.designation} onChange={e => handleEdit(e.target.value, index, 'designation')} />}
            </p>
            <p>
              <label>Reporting Manager</label>
              {employee.reportingManager && !showManager ?
              <span onClick={() => setManager({showManager: true})}>{employee.reportingManager}</span> :
              <input type="text" value={employee.reportingManager} onChange={e => handleEdit(e.target.value, index, 'reportingManager')} />}
            </p>
            <p>
              <label>Working Shift</label>
              {employee.shift && !showShift ?
              <span onClick={() => setShift({showShift: true})}>{employee.shift}</span> :
              <input type="text" value={employee.shift} onChange={e => handleEdit(e.target.value, index, 'shift')} />}
            </p>
            <div className="save-btn">
              <button>Save</button>
            </div>
          </div>
        </div>
      </div>
    </Fragment>
  );
}

export default EmployeeDetail;
