import { combineReducers } from 'redux';

const songsReducer = () => {
    return [
        { name: 'Sachin', employeeId: '3:15' },
        { name: 'Anshul', duration: '2:24' },
        { name: 'Olive Yew', duration: '2:24' },
        { name: 'Aida Bugg', duration: '2:24' },
        { name: 'Maureen Biologis', duration: '2:24' },
        { name: 'Allie Grater', duration: '2:24' },
    ];
};

const selectedSongReducer = (selectedSong = null, action) => {
    if (action.type === 'SONG_SELECTED') {
        return action.payload;
    } 
    return selectedSong;
}

export default combineReducers({
    songs: songsReducer,
    selectedSong: selectedSongReducer,
});
// now it passes to provider
