const initialValues = {
    employees: [
        { name: 'Sachin', employeeId: 2408 },
        { name: 'Anshul', employeeId: 7868 },
        { name: 'Olive Yew', employeeId: 9876 },
        { name: 'Aida Bugg', employeeId: 2345 },
        { name: 'Maureen Biologis', employeeId: 9086 },
        { name: 'Allie Grater', employeeId: 7735 },
    ],
  }

  const addNewEmployee = (state, payload) => {
    const newList = [...state, { name }];
    if (payload.todo) {
      newTodoList[payload.key] = newTodoList[payload.key] ? [...newTodoList[payload.key]] : [];
      newTodoList[payload.key].push(payload.todo);
    }
    return newTodoList;
  }

  function employeeReducer(state = initialValues, { payload, type }) {
    switch (type) {
      case "ADD_EMPLOYEE":
        return {
          ...state,
          employees: addNewEmployee(state, payload),
        };

      default:
        return state;
    }
  }

// const selectedSongReducer = (selectedSong = null, action) => {
