import React, { useState, useContext } from 'react';
import EmployeeForm from './addEmployee';
import EmployeeDetail from './employeeList';
import { useSelector } from 'react-redux';
import Store from "../store";
import '../css/styles.css';

function App() {
  const employeeList = useSelector(state => state.employees.employeesList);
  const { state, dispatch } = useContext(Store);
  const [employees, setEmployees] = useState('employeeList');
  console.log(state, 'state');
  console.log(employees, 'state');

  // const addEmployee = text => {
  //   const newEmployee = [...employees, { text }];
  //   setEmployees(newEmployee);
  // };

  // marking Absent of not present
  const attendence = index => {
    const newEmployee = [...employees];
    newEmployee[index].isAbsent = true;
    setEmployees(newEmployee);
  };

  // Remove Employee from List
  const removeEmployee = index => {
    const newEmployee = [...employees];
    newEmployee.splice(index, 1);
    setEmployees(newEmployee);
  };

  function addEmployee() {
    dispatch({ type: "ADD_TODO", payload: { employeeList } });
    setEmployees("");
  }


  return (
    <div className="app">
      <div className="ui middle aligned center aligned grid">
        <div class="column">
          <div className="container">
            <div className="container-inr">
              {employees.map((employee, index) => (
                <EmployeeDetail
                  key={index}
                  index={index}
                  employee={employee}
                  attendence={attendence}
                  removeEmployee={removeEmployee}
                />
              ))}
              <EmployeeForm addEmployee={addEmployee} />
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
