import React, { Fragment, useState } from 'react';
import { useInput } from './useInput';

const EmployeeDetail = ({ employee, closeModal, editEmployees, index }) => {
  const keys = {name: false, id: false, designation: false, manager: false, shift: false };
  const [showInput, setInput] = useState(keys);

  const { value:firstName, bind:bindFirstName, reset:resetFirstName } = useInput('');
  const { value:lastName, bind:bindLastName, reset:resetLastName } = useInput('');

  const handleSubmit = (evt) => {
    evt.preventDefault();
    alert(`Submitting Name ${firstName} ${lastName}`);
    resetFirstName();
    resetLastName();
}

  const handleEdit = (value, index, key) => {
    if (!value) return;
    editEmployees(value, index, key);
  };

  return (
    <Fragment>
      <div className="modal-container-outer" onClick={() => closeModal()}></div>
      <div className="modal-container employee-detail-container">
        <small onClick={() => closeModal()}>x</small>
        <h3>{employee.name}'s Detail</h3>
        <div className="employee-detail">
          <form onSubmit={handleSubmit}>
            <div className="employee-list">
              <p>
                <label>Employee Name</label>
                {!showInput.name ?
                  <span onClick={() => setInput({name: true})}>{firstName}</span> :
                  <input placeholder={employee.name} type="text" {...bindFirstName} />
                }
              </p>
              <p>
                <label>Employee ID</label>
                {employee.employeeId && !showInput.id ?
                <span onClick={() => setInput({id: true})}>{employee.employeeId}</span> :
                <input type="text" {...bindLastName} />}
              </p>
              <p>
                <label>Designation</label>
                {employee.designation && !showInput.designation ?
                <span onClick={() => setInput({designation: true})}>{employee.designation}</span> :
                <input type="text" placeholder={employee.designation} onChange={e => handleEdit(e.target.value, index, 'designation')} />}
              </p>
              <p>
                <label>Reporting Manager</label>
                {employee.reportingManager && !showInput.manager ?
                <span onClick={() => setInput({manager: true})}>{employee.reportingManager}</span> :
                <input type="text" placeholder={employee.reportingManager} onChange={e => handleEdit(e.target.value, index, 'reportingManager')} />}
              </p>
              <p>
                <label>Working Shift</label>
                {employee.shift && !showInput.shift ?
                <span onClick={() => setInput({shift: true})}>{employee.shift}</span> :
                <input type="text" placeholder={employee.shift} onChange={e => handleEdit(e.target.value, index, 'shift')} />}
              </p>
              <div className="save-btn">
                <button>Save</button>
              </div>
            </div>
            </form>
        </div>
      </div>
    </Fragment>
  );
}

export default EmployeeDetail;
