import React, { useState } from 'react';
import TodoForm from './addEmployee';
import Todo from './employeeList';
import { useSelector } from 'react-redux';
import '../css/styles.css';

function App() {
  const employeeList = useSelector(state => state.employees);
  const [todos, setTodos] = useState(employeeList);

  const addTodo = text => {
    const newTodos = [...todos, { text }];
    setTodos(newTodos);
  };

  // marking Absent of not present
  const attendence = index => {
    const newTodos = [...todos];
    newTodos[index].isAbsent = true;
    setTodos(newTodos);
  };

  // Remove Employee from List
  const removeEmployee = index => {
    const newTodos = [...todos];
    newTodos.splice(index, 1);
    setTodos(newTodos);
  };

  return (
    <div className="app">
      <div className="ui middle aligned center aligned grid">
        <div class="column">
          <div className="container">
            {todos.map((todo, index) => (
              <Todo
                key={index}
                index={index}
                todo={todo}
                attendence={attendence}
                removeEmployee={removeEmployee}
              />
            ))}
            <TodoForm addTodo={addTodo} />
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
