import React, { useState } from 'react';
import EmployeeForm from './addEmployee';
import EmployeeDetail from './employeeList';
// import { useSelector } from 'react-redux';
import { useDispatch } from 'react-redux'
import '../css/styles.css';

function App() {
  // const employeeList = useSelector(state => state.employeeList);
  const employeeList = [
    { name: 'Sachin', employeeId: 2408 },
    { name: 'Anshul', employeeId: 7868 },
    { name: 'Olive Yew', employeeId: 9876 },
    { name: 'Aida Bugg', employeeId: 2345 },
    { name: 'Maureen Biologis', employeeId: 9086 },
    { name: 'Allie Grater', employeeId: 7735 },
  ];
  const [employees, setEmployees] = useState([...employeeList]);
  const dispatch = useDispatch();
  console.log(employees, 'employees');

  const addEmployee = name => {
    const newEmployee = [...employees];
    const test = [...newEmployee,  name ];
    setEmployees(test);
    // dispatch({ type: 'ADD_EMPLOYEE', payload: employee });
  };

  // marking Absent of not present
  const attendence = index => {
    const newEmployee = [...employeeList];
    newEmployee[index].isAbsent = true;
    setEmployees(newEmployee);
    console.log(employees, 'employees');
  };

  // Remove Employee from List
  const removeEmployee = index => {
    // const newEmployee = [...employeeList];
    // newEmployee.splice(index, 1);
    // setEmployees(newEmployee);
    dispatch({ type: 'REMOVE_EMPLOYEE', payload: index });
  };


  return (
    <div className="app">
      <div className="ui middle aligned center aligned grid">
        <div class="column">
          <div className="container">
            <div className="container-inr">
              {employeeList.map((employee, index) => (
                <EmployeeDetail
                  key={index}
                  index={index}
                  employee={employee}
                  attendence={attendence}
                  removeEmployee={removeEmployee}
                />
              ))}
              <EmployeeForm addEmployee={addEmployee} />
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
