import React, { useState } from 'react';
import EmployeeForm from './addEmployee';
import EmployeeDetail from './employeeList';
import Building from './building';
import { useSelector } from 'react-redux';
import { useDispatch } from 'react-redux'
import Provider from '../context/provider';
import Context from "../context/context";
import '../css/styles.css';

function App() {
  const employeeList = useSelector(state => state.employeeList);
  const test = [...employeeList];
  const [employees, setEmployees] = useState(test);
  const dispatch = useDispatch();

  const addEmployee = name => {
    const employee = {name: name, isAbsent: false};
    dispatch({ type: 'ADD_EMPLOYEE', payload: employee });
  };

  // marking Absent of not present
  const attendence = index => {
    const newEmployee = [...employeeList];
    newEmployee[index].isAbsent = true;
    setEmployees(newEmployee);
    console.log(employees, 'employees');
  };

  // Remove Employee from List
  const removeEmployee = index => {
    dispatch({ type: 'REMOVE_EMPLOYEE', payload: index });
  };


  return (
    <Context.Consumer>
      {context => (
        <Fragment>
          <p>
            <strong>Project Name: </strong>
            {context.data.companyName}
          </p>
          <p>
            <strong>Manager Name: </strong>
            {context.data.recipientName}
          </p>
          <p>
            <strong>Duration: </strong>
            {context.data.package}
          </p>
          <p>
            <strong>Attendence: </strong>
            {context.data.attendence}
          </p>
          <button onClick={context.updateAttendenceStatus}>
            Update Attendence
          </button>
        </Fragment>
      )}
    </Context.Consumer>
  )
}

export default App;
