const initialValues = [
    { name: 'Sachin', employeeId: 1 },
    { name: 'Anshul', employeeId: 2 },
    { name: 'Olive Yew', employeeId: 3 },
    { name: 'Aida Bugg', employeeId: 4 },
    { name: 'Maureen Biologis', employeeId: 5 },
    { name: 'Allie Grater', employeeId: 6 },
  ];

const addNewEmployee = (state, name) => {
  const newList = [...state,  name ];
  return newList;
}

const removeEmployee = (state, index) => {
  const list = [...state];
  list.splice(index, 1);
  return list;
}

function employeeReducer(state = initialValues, { payload, type }) {
  switch (type) {
    case "ADD_EMPLOYEE":
      return addNewEmployee(state, payload);

    case "REMOVE_EMPLOYEE":
        return removeEmployee(state, payload);

    default:
      return state;
  }
}

export default combineReducers({
  employeeList: employeeReducer,
});
// now it passes to provider
