import React, { Fragment, useState } from 'react';
import { useSelector } from 'react-redux';

const EmployeeDetail = ({ employee, closeModal }) => {
  // const employeeList = useSelector(state => state.employeeList);
  const [showInput, setCount] = useState(false);

  const changeValue = (e) => {
    debugger;
  }

  return (
    <Fragment>
      <div className="modal-container-outer" onClick={() => closeModal()}></div>
      <div className="modal-container employee-detail-container">
        <small onClick={() => closeModal}>x</small>
        <h3>{employee.name}'s Detail</h3>
        <div className="employee-detail">
          <div className="employee-list">
            <p>
              <label>Employee Name</label>
              {!showInput ?
                <span onClick={() => setCount({showInput: true})}>{employee.name}</span> :
                <input type="text" value={employee.name} onChange={e => changeValue(e.target.value)} />
              }
            </p>
            <p>
              <label>Employee ID</label>
              {employee.employeeId ?
              <span>{employee.employeeId}</span> :
              <input type='text' />}
            </p>
            <p>
              <label>Designation</label>
              {employee.employeeId ?
              <span>{employee.employeeId}</span> :
              <input type='text' />}
            </p>
            <p>
              <label>Reporting Manager</label>
              {employee.reportingManager ?
              <span>{employee.reportingManager}</span> :
              <input type='text' />}
            </p>
            <p>
              <label>Working Shift</label>
              {employee.shift ?
              <span>{employee.shift}</span> :
              <input type='text' />}
            </p>
            <div className="save-btn">
              <button>Save</button>
            </div>
          </div>
        </div>
      </div>
    </Fragment>
  );
}

export default EmployeeDetail;
