import React, { Fragment, useState } from 'react';

const EmployeeDetail = ({ employee, closeModal, editEmployees, index }) => {
  const [showInput, setInput] = useState(false);
  const [showInputId, setInputId] = useState(false);

  const handleEdit = (index, value, key) => {
    if (!value) return;
    editEmployees(index, value, key);
  };

  const handleCheck = (e) => {
    if (e.target.id === 'empId') {
      showId = true;
    }
  };

  return (
    <Fragment>
      <div className="modal-container-outer" onClick={() => closeModal()}></div>
      <div className="modal-container employee-detail-container">
        <small onClick={() => closeModal()}>x</small>
        <h3>{employee.name}'s Detail</h3>
        <div className="employee-detail">
          <div className="employee-list">
            <p>
              <label>Employee Name</label>
              {!showInput ?
                <span onClick={() => setInput({showInput: true})}>{employee.name}</span> :
                <input type="text" value={employee.name} onChange={e => handleEdit(e.target.value, index, 'name')} />
              }
            </p>
            <p>
              <label>Employee ID</label>
              {employee.employeeId && !showInputId ?
              <span onClick={() => setInputId({showInputId: true})}>{employee.employeeId}</span> :
              <input type='text' />}
            </p>
            <p>
              <label>Designation</label>
              {employee.employeeId ?
              <span onClick={handleCheck}>{employee.employeeId}</span> :
              <input type='text' />}
            </p>
            <p>
              <label>Reporting Manager</label>
              {employee.reportingManager ?
              <span>{employee.reportingManager}</span> :
              <input type='text' />}
            </p>
            <p>
              <label>Working Shift</label>
              {employee.shift ?
              <span>{employee.shift}</span> :
              <input type='text' />}
            </p>
            <div className="save-btn">
              <button>Save</button>
            </div>
          </div>
        </div>
      </div>
    </Fragment>
  );
}

export default EmployeeDetail;
