function Todo({index, attendence,removeEmployee }) {
  const employeeList = useSelector(state => state.employees);
  return (
    <div
      className="todo"
      style={{ textDecoration: employeeList.isAbsent ? "line-through" : "" }}
    >
      {employeeList.text}
      <div>
        <button onClick={() => attendence(index)}>Absent</button>
        <button onClick={() => removeEmployee(index)}>Remove</button>
      </div>
    </div>
  );
};

export default Todo;
